package codewars.kyu4.morse_code_decoder;

import org.junit.Test;

import static org.hamcrest.CoreMatchers.is;
import static org.junit.Assert.assertThat;

public class MorseCodeDecoderTest {
    @Test
    public void testExampleFromDescription() throws Exception {
        assertThat(MorseCodeDecoder.decodeMorse(MorseCodeDecoder.decodeBits("")), is("THE QUICK BROWN FOX JUMPS OVER THE LAZY DOG."));
    }
}